targets.query:
from(bucket: "vmatrix")
  |> range(start: v.timeRangeStart, stop: v.timeRangeStop)
  |> filter(fn: (r) => r._measurement == "system.sysinfo" and r._field == "freeram")
  |> filter(fn: (r) => contains(value: r.ip, set: ${machines:json}))
  |> aggregateWindow(every: v.windowPeriod, fn: min, createEmpty: false)
  |> drop(columns: ["_field"])

